import logo from "./logo.svg";

const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  );
};

const Part = (props) => {
  return (
    <>
      <p>
        {props.name} {props.number}
      </p>
    </>
  );
};

const Content = (props) => {
  return (
    <>
      <Part name={props.part1Name} number={props.part1Excercises} />
      <Part name={props.part2Name} number={props.part2Excercises} />
      <Part name={props.part2Name} number={props.part3Excersises} />
    </>
  );
};

const Total = (props) => {
  return (
    <>
      <p>
        Number of exercise{" "}
        {props.exercises1 + props.exercises2 + props.exercises3}
      </p>
    </>
  );
};
const App = () => {
  const course = "Half Stack application development";
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10,
    },
    {
      name: "Using props to pass data",
      excercises: 7,
    },
    {
      name: "State of component",
      excercises: 14,
    },
  ];

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  );
};
export default App;
