import logo from "./logo.svg";

const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  );
};

const Part = (props) => {
  return (
    <>
      <p>
        {props.name} {props.number}
      </p>
    </>
  );
};

const Content = (props) => {
  return (
    <>
      <Part name={props.part1Name} number={props.part1Excercises} />
      <Part name={props.part2Name} number={props.part2Excercises} />
      <Part name={props.part2Name} number={props.part3Excersises} />
    </>
  );
};

const Total = (props) => {
  return (
    <>
      <p>
        Number of exercise{" "}
        {props.exercises1 + props.exercises2 + props.exercises3}
      </p>
    </>
  );
};
const App = () => {
  const course = "Half Stack application development";
  const part1 = {
    name: "Fundamentals of React",
    exercises: 10,
  };

  const part2 = {
    name: "Using props to pass data",
    excercises: 7,
  };

  const part3 = {
    name: "State of component",
    excercises: 14,
  };

  return (
    <div>
      <Header course={course} />
      <Content
        part1Name={part1.name}
        part2Name={part2.name}
        part3Name={part3.name}
        part1Excercises={part1.exercises}
        part2Excercises={part2.excercises}
        part3Excersises={part3.excercises}
        p
      />
      <Total
        exercises1={part1.exercises}
        exercises2={part2.excercises}
        exercises3={part3.excercises}
      />
    </div>
  );
};
export default App;
